import Vue from "vue";
import VueRouter from "vue-router";
import station from "./../Station.vue";
import store from '../store/main.js'
import header from "./../components/Index/header";
Vue.use(VueRouter);

export default new VueRouter({
  base: "/",
  mode: "history",
  routes: [
    {
      path: "/",
      components: {
        header: header
      }
    },
    {
      path: "/details/:recentStopId",
      name: "details",
      components: { details: station }
    }
  ],
  scrollBehavior(to, from, savedPosition) {
    if (savedPosition) {
      return savedPosition;
    } else {
      let position = {}
      if(store.getters.recentStop) {
        position.selector = "#" + store.getters.recentStop.id
      if (store.getters.calculatePageOffset) {
        position.offset = {
          x: 0,
          y: store.getters.calculatePageOffset 
        };
      }
      }

      return position;
    }
  }
});
